glsl3.vert:
precision highp float;
uniform CCGlobal {
  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;
  vec4 cc_time;
  mediump vec4 cc_screenSize;
  mediump vec4 cc_screenScale;
};
in vec3 a_position;
in vec2 a_uv0;
in vec4 a_color;
out vec2 v_uv0;
out vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl3.frag:
precision highp float;
in vec2 v_uv0;
in vec4 v_color;
uniform sampler2D texture;
uniform Properties {
  vec2 center;
  float ratio;
  float radius;
  float feather;
};
void main () {
  vec4 color = v_color;
  color *= texture(texture, v_uv0);
  if (color.a == 0.0) discard;
  float dis = distance(vec2(v_uv0.x, v_uv0.y / ratio), vec2(center.x, center.y / ratio));
  color.a = smoothstep(radius, radius - feather, dis);
  color.a *= v_color.a;
  gl_FragColor = color;
}

glsl1.vert:
precision highp float;
uniform mat4 cc_matViewProj;
attribute vec3 a_position;
attribute vec2 a_uv0;
attribute vec4 a_color;
varying vec2 v_uv0;
varying vec4 v_color;
void main () {
  gl_Position = cc_matViewProj * vec4(a_position, 1);
  v_uv0 = a_uv0;
  v_color = a_color;
}

glsl1.frag:
precision highp float;
varying vec2 v_uv0;
varying vec4 v_color;
uniform sampler2D texture;
uniform vec2 center;
uniform float ratio;
uniform float radius;
uniform float feather;
void main () {
  vec4 color = v_color;
  color *= texture2D(texture, v_uv0);
  if (color.a == 0.0) discard;
  float dis = distance(vec2(v_uv0.x, v_uv0.y / ratio), vec2(center.x, center.y / ratio));
  color.a = smoothstep(radius, radius - feather, dis);
  color.a *= v_color.a;
  gl_FragColor = color;
}
